mapper.select:
select
  id, projectrelated, projectfeatures, tokenomics, pid
from project_details


        and id = #{id}


        and projectrelated = #{projectrelated}


        and projectfeatures = #{projectfeatures}


        and tokenomics = #{tokenomics}


        and pid = #{pid}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from project_details


        and id = #{id}


        and projectrelated = #{projectrelated}


        and projectfeatures = #{projectfeatures}


        and tokenomics = #{tokenomics}


        and pid = #{pid}
