mapper.select:
SELECT COUNT(*)
FROM registration
WHERE personal_score > (
SELECT personal_score
FROM registration
WHERE id = #{id}
)
OR (personal_score = (
SELECT personal_score
FROM registration
WHERE id = #{id}
) AND id < #{id});

